import { useRef, useEffect, useState } from 'react';
import { createSmoothHorizontalScroller } from '../../utils/smoothHorizontalScroll';
import Mascot from '../mascot/Mascot';

interface HorizontalScrollContainerProps {
    children: React.ReactNode;
    scrollerRef?: React.RefObject<HTMLDivElement | null>;
    isDesktop?: boolean;
    onLandingComplete?: () => void;
    startLanding?: boolean;
}

const HorizontalScrollContainer = ({
    children,
    scrollerRef: externalScrollerRef,
    isDesktop = true,
    onLandingComplete,
    startLanding = true
}: HorizontalScrollContainerProps) => {
    const containerRef = useRef<HTMLDivElement>(null);
    const internalScrollerRef = useRef<HTMLDivElement>(null);
    // Use external ref if provided, otherwise use internal
    const scrollerRef = (externalScrollerRef || internalScrollerRef) as React.RefObject<HTMLDivElement>;



    // State to track mascot landing animation
    const [isMascotLanding, setIsMascotLanding] = useState(true);

    useEffect(() => {
        const container = scrollerRef.current;
        if (!container) return;

        let cleanupScroller = () => { };
        let scrollTo = (pos: number, _immediate = false, _duration = 1000) => { container.scrollLeft = pos; }; // Default

        if (isDesktop) {
            const scroller = createSmoothHorizontalScroller(container);
            cleanupScroller = scroller.cleanup;
            scrollTo = scroller.scrollTo;
        }

        const handleNavigate = (e: CustomEvent<{ sectionId: string }>) => {
            const section = document.getElementById(e.detail.sectionId);
            if (section) {
                if (isDesktop) {
                    // Use a longer duration (1500ms) for a more cinematic feel
                    scrollTo(section.offsetLeft, false, 1500);
                } else {
                    section.scrollIntoView({ behavior: 'smooth', block: 'start' });
                }
            }
        };

        window.addEventListener('navigate-section', handleNavigate as EventListener);

        return () => {
            cleanupScroller();
            window.removeEventListener('navigate-section', handleNavigate as EventListener);
        };
    }, [isDesktop, scrollerRef]);

    return (
        <div ref={containerRef} className="relative h-[100dvh] lg:h-screen lg:overflow-hidden">
            <div
                ref={scrollerRef}
                className={`horizontal-scroller flex w-full lg:h-full overflow-x-auto overflow-y-hidden scroll-smooth flex-row transition-opacity duration-1000 ease-in-out ${isMascotLanding ? 'opacity-0' : 'opacity-100'}`}
                style={isDesktop ? {
                    scrollBehavior: 'auto', // Let JS handle smoothing
                    willChange: 'transform, scroll-position',
                    transform: 'translateZ(0)' // GPU acceleration
                } : {
                    scrollBehavior: 'smooth', // Native smooth scroll for mobile
                    willChange: 'scroll-position'
                }}
            >
                {children}
            </div>

            <Mascot
                containerRef={scrollerRef}
                startLanding={startLanding && isMascotLanding}
                onLandingComplete={() => {
                    setIsMascotLanding(false);
                    onLandingComplete?.();
                }}
            />
        </div>
    );
};

export default HorizontalScrollContainer;

import { useEffect, useRef, useState, useCallback } from 'react';
import lottie, { type AnimationItem } from 'lottie-web';
import gsap from 'gsap';
import walkData from '../../assets/walk.json';
import idleData from '../../assets/idle.json';
import landingData from '../../assets/landing.json';
import landingMobileData from '../../assets/mobile/Landing_mobile.json';
import useMediaQuery from '../../hooks/useMediaQuery';

interface MascotProps {
    containerRef: React.RefObject<HTMLDivElement | null>;
    startLanding?: boolean;
}

const Mascot = ({ containerRef, onLandingComplete, startLanding = true }: MascotProps & { onLandingComplete?: () => void }) => {
    const walkContainerRef = useRef<HTMLDivElement>(null);
    const idleContainerRef = useRef<HTMLDivElement>(null);
    const landingContainerRef = useRef<HTMLDivElement>(null);
    const walkAnimRef = useRef<AnimationItem | null>(null);
    const idleAnimRef = useRef<AnimationItem | null>(null);
    const landingAnimRef = useRef<AnimationItem | null>(null);
    const wrapperRef = useRef<HTMLDivElement>(null);

    const [isWalking, setIsWalking] = useState(false);
    const [isLanding, setIsLanding] = useState(true);

    const isDesktop = useMediaQuery('(min-width: 1024px)');
    const mascotSize = isDesktop ? 150 : 100;

    const scrollTimeout = useRef<number | null>(null);

    // Watch for startLanding signal
    useEffect(() => {
        if (startLanding && landingAnimRef.current && landingAnimRef.current.isPaused) {
            landingAnimRef.current.play();
        }
    }, [startLanding]);

    const handleLandingComplete = useCallback(() => {
        if (!wrapperRef.current) return;



        // Notify parent
        if (onLandingComplete) onLandingComplete();

        setIsLanding(false);
        if (wrapperRef.current) {
            gsap.set(wrapperRef.current, { clearProps: "all" });
        }
    }, [onLandingComplete]);

    // Initialize animations
    useEffect(() => {
        // --- Walk Animation ---
        if (walkContainerRef.current) {
            try {
                if (walkAnimRef.current) walkAnimRef.current.destroy();
                walkAnimRef.current = lottie.loadAnimation({
                    container: walkContainerRef.current,
                    renderer: 'svg',
                    loop: true,
                    autoplay: false,
                    animationData: walkData,
                    rendererSettings: { preserveAspectRatio: 'xMidYMid meet' }
                });
            } catch (error) {
                console.error("Mascot: Failed to load walk animation", error);
            }
        }

        // --- Idle Animation ---
        if (idleContainerRef.current) {
            try {
                if (idleAnimRef.current) idleAnimRef.current.destroy();
                idleAnimRef.current = lottie.loadAnimation({
                    container: idleContainerRef.current,
                    renderer: 'svg',
                    loop: true,
                    autoplay: true,
                    animationData: idleData,
                    rendererSettings: { preserveAspectRatio: 'xMidYMid meet' }
                });
            } catch (error) {
                console.error("Mascot: Failed to load idle animation", error);
            }
        }

        // --- Landing Animation ---
        if (landingContainerRef.current) {
            try {
                const animationData = isDesktop ? landingData : landingMobileData;

                if (landingAnimRef.current) landingAnimRef.current.destroy();
                landingAnimRef.current = lottie.loadAnimation({
                    container: landingContainerRef.current,
                    renderer: 'svg',
                    loop: false,
                    autoplay: false, // Wait for signal
                    animationData: animationData,
                    rendererSettings: { preserveAspectRatio: 'xMidYMid meet' }
                });

                landingAnimRef.current.addEventListener('DOMLoaded', () => {
                    if (startLanding) {
                        landingAnimRef.current?.play();
                    }
                });

                landingAnimRef.current.addEventListener('complete', () => {
                    handleLandingComplete();
                });

            } catch (error) {
                console.error("Mascot: Failed to load landing animation", error);
            }
        }

        return () => {
            walkAnimRef.current?.destroy();
            idleAnimRef.current?.destroy();
            landingAnimRef.current?.destroy();
        };
    }, [handleLandingComplete, startLanding, isDesktop]); // Re-run if landing handler changes



    // Initial setup for landing position
    useEffect(() => {
        if (isLanding && wrapperRef.current) {

            // Start fullscreen
            gsap.set(wrapperRef.current, {
                position: 'fixed',
                left: 0,
                top: 0,
                width: '100vw',
                height: '100vh',
                xPercent: 0,
                yPercent: 0,
                bottom: 'auto',
                zIndex: 9999
            });
        }
    }, [isLanding]); // Run when isLanding status is available

    const lastScrollPos = useRef(0);
    const facingRight = useRef(true);

    // Scroll Handler
    useEffect(() => {
        if (isLanding) return; // Disable scroll movement during landing

        const container = containerRef.current;
        if (!container) return;

        const handleScroll = (isInitial = false) => {
            const scrollPos = isDesktop ? container.scrollLeft : container.scrollTop;
            const maxScroll = isDesktop
                ? container.scrollWidth - container.clientWidth
                : container.scrollHeight - container.clientHeight;
            const progress = maxScroll > 0 ? scrollPos / maxScroll : 0;

            const diff = scrollPos - lastScrollPos.current;
            if (Math.abs(diff) > 0.5) {
                if (diff > 0) facingRight.current = true;
                else if (diff < 0) facingRight.current = false;
            }
            lastScrollPos.current = scrollPos;

            const walkAnim = walkAnimRef.current;
            if (walkAnim && walkAnim.isLoaded) {
                const totalFrames = walkAnim.totalFrames > 0 ? walkAnim.totalFrames : 60;
                const pixelsPerFrame = 15;
                let frame = (scrollPos / pixelsPerFrame) % totalFrames;
                if (!facingRight.current) frame = (totalFrames - frame) % totalFrames;
                walkAnim.goToAndStop(frame, true);
            }

            const currentMascotSize = window.innerWidth < 1024 ? 100 : 150;
            const currentPadding = window.innerWidth < 1024 ? 16 : 32;
            const moveDistanceOfScreen = window.innerWidth - currentMascotSize - (currentPadding * 2);

            if (wrapperRef.current) {
                wrapperRef.current.style.transform = `translateX(${progress * moveDistanceOfScreen}px) scaleX(${facingRight.current ? 1 : -1})`;
            }

            if (!isInitial) {
                if (!isWalking) setIsWalking(true);
                if (scrollTimeout.current) window.clearTimeout(scrollTimeout.current);
                scrollTimeout.current = window.setTimeout(() => setIsWalking(false), 50);
            }
        };

        handleScroll(true);
        const onScroll = () => handleScroll(false);
        container.addEventListener('scroll', onScroll, { passive: true });
        return () => {
            container.removeEventListener('scroll', onScroll);
            if (scrollTimeout.current) window.clearTimeout(scrollTimeout.current);
        };
    }, [containerRef, isWalking, isLanding]);

    return (
        <div
            ref={wrapperRef}
            className={`fixed lg:absolute z-[9999] pointer-events-none ${isLanding ? '' : 'left-4 lg:left-8'}`}
            style={{
                // While landing, we let GSAP control size/pos. 
                // After landing, we use these styles as base.
                bottom: isLanding ? 'auto' : '4px',
                width: isLanding ? '100vw' : `${mascotSize}px`,
                height: isLanding ? '100vh' : `${mascotSize}px`,
                transition: isLanding ? 'none' : 'transform 75ms ease-linear'
            }}
        >
            {/* Landing Container */}
            <div
                ref={landingContainerRef}
                className={`w-full h-full absolute top-0 left-0 transition-opacity duration-0 ${isLanding ? 'opacity-100' : 'opacity-0'}`}
                style={{
                    transform: isLanding ? 'scale(1.4)' : 'scale(1)',
                    transformOrigin: 'center center'
                }}
            />

            {/* Walk Container */}
            <div
                ref={walkContainerRef}
                className={`w-full h-full absolute top-0 left-0 transition-opacity duration-0 ${isWalking && !isLanding ? 'opacity-100' : 'opacity-0'}`}
            />

            {/* Idle Container */}
            <div
                ref={idleContainerRef}
                className={`w-full h-full absolute top-0 left-0 transition-opacity duration-0 ${!isWalking && !isLanding ? 'opacity-100' : 'opacity-0'}`}
            />
        </div>
    );
};

export default Mascot;

import clsx from 'clsx';
import { useEffect, useState } from 'react';
import { supabase } from '../../lib/supabaseClient';

function Portfolio({
    id = "portfolio", className
}: { id?: string; className?: string; }) {
    const [videos, setVideos] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchVideos = async () => {
            try {
                // Check for environment variables
                const supabaseUrl = import.meta.env.VITE_SUPABASE_URL;
                const supabaseKey = import.meta.env.VITE_SUPABASE_ANON_KEY;

                if (!supabaseUrl || !supabaseKey) {
                    throw new Error("Missing Supabase environment variables! Check your .env file or build configuration.");
                }

                const { data, error } = await supabase.from('portfolio').select('*').limit(6);

                if (error) throw error;

                if (data) {
                    console.log('Portfolio videos:', data);
                    setVideos(data);
                }
            } catch (err: any) {
                console.error('Error fetching portfolio:', err);
                setError(err.message || 'Failed to load videos');
            } finally {
                setLoading(false);
            }
        };
        fetchVideos();
    }, []);

    const getYoutubeId = (url: string) => {
        if (!url) return null;
        const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
        const match = url.match(regExp);
        return (match && match[2].length === 11) ? match[2] : null;
    };

    return (
        <section
            id={id}
            className={clsx("min-h-[100dvh] lg:h-[100dvh] w-full lg:w-screen flex flex-col bg-background flex-shrink-0 relative", className)}
            style={{ paddingTop: '100px', paddingBottom: '130px' }}
        >
            {/* Hide scrollbar CSS */}
            <style>{`
                .portfolio-scroll::-webkit-scrollbar { display: none; }
                .portfolio-scroll { -ms-overflow-style: none; scrollbar-width: none; }
                .portfolio-bento {
                    display: grid;
                    grid-template-columns: 1fr;
                    grid-auto-rows: 250px;
                    gap: 16px;
                }
                @media (min-width: 768px) {
                    .portfolio-bento {
                        grid-template-columns: repeat(3, minmax(0, 1fr));
                        grid-auto-rows: calc((100vh - 520px) / 2);
                        gap: 20px;
                    }
                }
            `}</style>

            {/* HEADING */}
            <div className="flex-shrink-0 text-center px-4 md:px-8" style={{ position: 'relative', zIndex: 10, marginBottom: '30px', marginTop: '10px' }}>
                <h2
                    className="font-black uppercase tracking-[0.2em] text-center [text-shadow:2px_2px_0_white] md:[text-shadow:4px_4px_0_white]"
                    style={{ fontSize: 'clamp(2rem, 8vw, 3.5rem)', color: 'black' }}
                >
                    OUR PORTFOLIO
                </h2>
                <div className="w-[80vw] md:w-[960px]" style={{ height: '4px', background: '#000000', margin: '2px auto 0', borderRadius: '2px', maxWidth: '100%' }} />
            </div>

            {/* SCROLLABLE VIDEO CONTAINER */}
            <div className="flex-1 lg:overflow-y-auto overflow-y-visible overflow-x-hidden portfolio-scroll mb-8" style={{ background: 'rgba(254, 168, 0, 0.85)', borderRadius: '20px', maxWidth: '96%', margin: '0 auto' }}>
                <div className="w-full mx-auto p-4 md:p-[40px]">
                    {loading ? (
                        <div className="text-center text-white/50 py-10">
                            <p>Loading portfolio...</p>
                        </div>
                    ) : error ? (
                        <div className="text-center text-red-500 py-10">
                            <p className="font-bold">Error loading videos:</p>
                            <p>{error}</p>
                            <p className="text-sm mt-2 text-white/60">Check console for details.</p>
                        </div>
                    ) : (!videos || videos.length === 0) ? (
                        <div className="text-center text-white/50 py-10">
                            <p>No videos found in the portfolio.</p>
                        </div>
                    ) : (
                        <div className="portfolio-bento">
                            {videos.map((video, index) => {
                                if (!video) return null;

                                const videoInitial = String(video.youtube_url || video.link || video.url || video.video_link || video.video_url || "");
                                const videoId = getYoutubeId(videoInitial);

                                let gridClass = "col-span-1 row-span-1";

                                if (index === 0) {
                                    gridClass = "md:col-span-2 md:row-span-2"; // Big top-left
                                } else if (index === 1) {
                                    gridClass = "md:col-start-3 md:row-start-1"; // Small top-right
                                } else if (index === 2) {
                                    gridClass = "md:col-start-3 md:row-start-2"; // Small mid-right
                                } else if (index === 3) {
                                    gridClass = "md:col-start-1 md:row-start-3"; // Small bottom-left
                                } else if (index === 4) {
                                    gridClass = "md:col-start-1 md:row-start-4"; // Small bottom-left
                                } else if (index === 5) {
                                    gridClass = "md:col-span-2 md:row-span-2 md:col-start-2 md:row-start-3"; // Big bottom-right
                                }

                                if (!videoId) return null;

                                return (
                                    <div
                                        key={video.id || index}
                                        className={clsx(
                                            "relative overflow-hidden rounded-2xl bg-black/30 border border-white/10 p-2 transition-all duration-300 hover:border-yellow-400/60",
                                            gridClass
                                        )}
                                    >
                                        <div className="w-full h-full overflow-hidden rounded-xl">
                                            <iframe
                                                src={`https://www.youtube.com/embed/${videoId}`}
                                                title={video.Title || `Portfolio Video ${index + 1}`}
                                                className="w-full h-full object-cover"
                                                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                                allowFullScreen
                                            />
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                    )}
                </div>
            </div>
        </section>
    );
};
export default Portfolio;

